/* Estilos para la p√°gina del carrito */
.cart-section {
    padding: 40px 0;
    min-height: 400px;
}

.cart-section h1 {
    margin-bottom: 30px;
    color: var(--primary-color);
}

.cart-table {
    width: 100%;
    border-collapse: collapse;
    margin-bottom: 30px;
}

.cart-table th {
    background-color: var(--light-gray);
    padding: 12px;
    text-align: left;
    font-weight: bold;
}

.cart-table td {
    padding: 12px;
    border-bottom: 1px solid var(--light-gray);
}

.product-info {
    display: flex;
    align-items: center;
}

.product-info img {
    width: 80px;
    height: 80px;
    object-fit: contain;
    margin-right: 15px;
}

.product-info h4 {
    margin: 0;
    font-size: 1rem;
}

.quantity-control {
    display: flex;
    align-items: center;
}

.quantity-btn {
    width: 30px;
    height: 30px;
    background-color: var(--light-gray);
    border: none;
    border-radius: 50%;
    cursor: pointer;
    font-weight: bold;
    font-size: 1.2rem;
}

.quantity-input {
    width: 40px;
    text-align: center;
    margin: 0 10px;
    padding: 5px;
    border: 1px solid var(--dark-gray);
    border-radius: var(--border-radius);
}

.remove-btn {
    background: none;
    border: none;
    color: var(--danger);
    cursor: pointer;
    font-size: 1.2rem;
}

.cart-actions {
    display: flex;
    justify-content: space-between;
    margin-top: 20px;
}

.cart-summary {
    background-color: var(--white);
    padding: 20px;
    border-radius: var(--border-radius);
    box-shadow: var(--shadow);
    margin-top: 20px;
}

.total {
    display: flex;
    justify-content: space-between;
    font-size: 1.2rem;
    font-weight: bold;
}

.empty-cart {
    text-align: center;
    padding: 40px 0;
}

.empty-cart p {
    margin-bottom: 20px;
    font-size: 1.2rem;
}

.notification {
    position: fixed;
    top: 20px;
    right: 20px;
    padding: 15px 25px;
    background-color: var(--success);
    color: var(--white);
    border-radius: var(--border-radius);
    box-shadow: var(--shadow);
    opacity: 0;
    transform: translateY(-20px);
    transition: opacity 0.3s ease, transform 0.3s ease;
    z-index: 1000;
}

.notification.show {
    opacity: 1;
    transform: translateY(0);
}

css.products.css:
/* Estilos para tarjetas de productos */
.product-card {
    background-color: var(--white);
    border-radius: var(--border-radius);
    box-shadow: var(--shadow);
    overflow: hidden;
    transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.product-card:hover {
    transform: translateY(-5px);
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.2);
}

.product-image {
    height: 200px;
    overflow: hidden;
}

.product-image img {
    width: 100%;
    height: 100%;
    object-fit: contain;
    transition: transform 0.3s ease;
}

.product-card:hover .product-image img {
    transform: scale(1.05);
}

.product-details {
    padding: 15px;
}

.product-title {
    font-size: 1rem;
    font-weight: bold;
    margin-bottom: 10px;
    height: 40px;
    overflow: hidden;
    display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
}

.product-price {
    font-size: 1.2rem;
    color: var(--primary-color);
    font-weight: bold;
    margin-bottom: 10px;
}

.product-price .original-price {
    text-decoration: line-through;
    color: var(--dark-gray);
    font-size: 0.9rem;
    margin-right: 5px;
}

.add-to-cart {
    width: 100%;
    text-align: center;
}

/* Estilos para el detalle de producto */
.product-layout {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 30px;
    margin-bottom: 30px;
}

@media (max-width: 768px) {
    .product-layout {
        grid-template-columns: 1fr;
    }
}

.product-images {
    position: relative;
}

.main-image {
    border: 1px solid var(--light-gray);
    border-radius: var(--border-radius);
    overflow: hidden;
    margin-bottom: 10px;
    height: 400px;
}

.main-image img {
    width: 100%;
    height: 100%;
    object-fit: contain;
}

.thumbnails {
    display: flex;
    gap: 10px;
    overflow-x: auto;
}

.thumbnail {
    width: 60px;
    height: 60px;
    border: 1px solid var(--light-gray);
    border-radius: var(--border-radius);
    cursor: pointer;
    overflow: hidden;
}

.thumbnail img {
    width: 100%;
    height: 100%;
    object-fit: cover;
}

.thumbnail.active {
    border-color: var(--primary-color);
}

.product-info h1 {
    font-size: 1.8rem;
    margin-bottom: 15px;
    color: var(--text-color);
}

.product-code {
    color: var(--dark-gray);
    margin-bottom: 10px;
}

.product-price-detail {
    font-size: 1.8rem;
    color: var(--primary-color);
    margin-bottom: 15px;
}

.product-price-detail .original-price {
    text-decoration: line-through;
    color: var(--dark-gray);
    font-size: 1.2rem;
    margin-right: 10px;
}

.product-description {
    margin-bottom: 20px;
}

.product-quantity {
    display: flex;
    align-items: center;
    margin-bottom: 20px;
}

.quantity-input {
    width: 60px;
    text-align: center;
    margin: 0 10px;
    padding: 5px;
    border: 1px solid var(--dark-gray);
    border-radius: var(--border-radius);
}

.quantity-btn {
    width: 30px;
    height: 30px;
    background-color: var(--light-gray);
    border: none;
    border-radius: 50%;
    cursor: pointer;
    font-weight: bold;
    font-size: 1.2rem;
}

.add-to-cart-detail {
    margin-top: 20px;
}

.product-specs {
    background-color: var(--white);
    padding: 20px;
    border-radius: var(--border-radius);
    margin-top: 30px;
}

.product-specs h3 {
    margin-bottom: 15px;
    color: var(--primary-color);
}

.specs-table {
    width: 100%;
    border-collapse: collapse;
}

.specs-table tr:nth-child(even) {
    background-color: var(--light-gray);
}

.specs-table th, .specs-table td {
    padding: 10px;
    text-align: left;
    border-bottom: 1px solid var(--light-gray);
}

.specs-table th {
    width: 40%;
    font-weight: bold;
}

/* Categor√≠as */
.category-card {
    position: relative;
    height: 150px;
    border-radius: var(--border-radius);
    overflow: hidden;
}

.category-card img {
    width: 100%;
    height: 100%;
    object-fit: cover;
}

.category-overlay {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(0, 0, 0, 0.5);
    display: flex;
    justify-content: center;
    align-items: center;
    transition: background-color 0.3s ease;
}

.category-card:hover .category-overlay {
    background-color: rgba(0, 0, 0, 0.7);
}

.category-name {
    color: var(--white);
    font-size: 1.2rem;
    font-weight: bold;
    text-align: center;
    padding: 10px;
}

css.styles.css:
/* Estilos Globales */
:root {
    --primary-color: #0066cc;
    --secondary-color: #ff6600;
    --accent-color: #ffcc00;
    --text-color: #333333;
    --light-gray: #f4f4f4;
    --dark-gray: #666666;
    --white: #ffffff;
    --black: #000000;
    --success: #28a745;
    --danger: #dc3545;
    --border-radius: 4px;
    --shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
}

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: Arial, Helvetica, sans-serif;
    line-height: 1.6;
    color: var(--text-color);
    background-color: var(--light-gray);
}

.container {
    width: 100%;
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 15px;
}

a {
    text-decoration: none;
    color: var(--primary-color);
}

ul {
    list-style: none;
}

.btn {
    display: inline-block;
    padding: 10px 20px;
    background-color: var(--secondary-color);
    color: var(--white);
    border: none;
    border-radius: var(--border-radius);
    cursor: pointer;
    font-weight: bold;
    transition: background-color 0.3s ease;
}

.btn:hover {
    background-color: var(--accent-color);
}

img {
    max-width: 100%;
    height: auto;
}

/* Secciones */
section {
    padding: 40px 0;
}

section h2 {
    text-align: center;
    margin-bottom: 30px;
    color: var(--primary-color);
    position: relative;
}

section h2::after {
    content: '';
    display: block;
    width: 50px;
    height: 3px;
    background-color: var(--secondary-color);
    margin: 10px auto;
}

/* Grids */
.products-grid, .categories-grid {
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    gap: 20px;
}

@media (max-width: 992px) {
    .products-grid, .categories-grid {
        grid-template-columns: repeat(3, 1fr);
    }
}

@media (max-width: 768px) {
    .products-grid, .categories-grid {
        grid-template-columns: repeat(2, 1fr);
    }
}

@media (max-width: 576px) {
    .products-grid, .categories-grid {
        grid-template-columns: 1fr;
    }
}

/* Banner */
.banner {
    position: relative;
    height: 400px;
    overflow: hidden;
}

.banner img {
    width: 100%;
    height: 100%;
    object-fit: cover;
}

.banner-text {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    text-align: center;
    color: var(--white);
    background-color: rgba(0, 0, 0, 0.6);
    padding: 20px;
    border-radius: var(--border-radius);
    width: 80%;
    max-width: 600px;
}

.banner-text h1 {
    font-size: 2.5rem;
    margin-bottom: 10px;
}

.banner-text p {
    font-size: 1.2rem;
    margin-bottom: 20px;
}

/* Breadcrumb */
.breadcrumb {
    background-color: var(--white);
    padding: 10px 0;
    margin-bottom: 20px;
    box-shadow: var(--shadow);
}

.breadcrumb ul {
    display: flex;
    flex-wrap: wrap;
}

.breadcrumb li {
    margin-right: 10px;
}

.breadcrumb li:not(:last-child)::after {
    content: '>';
    margin-left: 10px;
    color: var(--dark-gray);
}

.breadcrumb a:hover {
    text-decoration: underline;
}

/* Formularios */
.form-group {
    margin-bottom: 15px;
}

.form-group label {
    display: block;
    margin-bottom: 5px;
    font-weight: bold;
}

.form-control {
    width: 100%;
    padding: 10px;
    border: 1px solid var(--dark-gray);
    border-radius: var(--border-radius);
}

/* Alertas */
.alert {
    padding: 10px 15px;
    margin-bottom: 15px;
    border-radius: var(--border-radius);
}

.alert-success {
    background-color: var(--success);
    color: var(--white);
}

.alert-danger {
    background-color: var(--danger);
    color: var(--white);
}

