// URL base de la API
const API_URL = 'http://localhost:3000/api';


let isFetching = false;


function getBaseUrl() {
    // Obtener la URL completa actual
    const currentUrl = window.location.href;
    console.log('URL actual:', currentUrl);
    
    // Verificar si estamos en index.html en la ra√≠z o en una subcarpeta
    if (currentUrl.includes('/index.html')) {
        if (currentUrl.includes('/ferremas-ecommerce/') || 
            currentUrl.includes('/pages/')) {
            // Estamos en una subcarpeta
            return './';
        } else {
            // Estamos en la ra√≠z
            return 'ferremas-ecommerce/pages/';
        }
    } else if (currentUrl.includes('/ferremas-ecommerce/pages/')) {
        // Estamos en alguna p√°gina dentro de /ferremas-ecommerce/pages/
        return './';
    } else {
        // Por defecto, asumir que estamos en la ra√≠z
        return 'ferremas-ecommerce/pages/';
    }
}

// Luego usar esta funci√≥n para construir URLs
function getProductUrl(productId) {
    return `${getBaseUrl()}products.html?id=${productId}`;
}

function getCategoryUrl(categoryId) {
    return `${getBaseUrl()}categorias.html?id=${categoryId}`;
}


// Funci√≥n para obtener datos de la API
async function fetchAPI(endpoint) {
    try {
        console.log('Fetching:', API_URL + endpoint); // Debug
        const response = await fetch(`${API_URL}${endpoint}`);
        
        if (!response.ok) {
            throw new Error(`Error: ${response.status}`);
        }
        
        return await response.json();
    } catch (error) {
        console.error('Error en la API:', error);
        // Devuelve un array vac√≠o o un objeto b√°sico seg√∫n el contexto para evitar errores
        return endpoint.includes('productos') ? [] : {};
    }
}

// Obtener todas las categor√≠as
async function getCategorias() {
    try {
        return await fetchAPI('/categorias');
    } catch (error) {
        console.error('Error al obtener categor√≠as:', error);
        // Devolver datos de muestra
        return [
            { id: 1, nombre: 'Herramientas', slug: 'herramientas' },
            { id: 2, nombre: 'Herramientas Manuales', slug: 'herramientas-manuales' },
            { id: 3, nombre: 'Herramientas El√©ctricas', slug: 'herramientas-electricas' },
            { id: 4, nombre: 'Materiales de Construcci√≥n', slug: 'materiales-construccion' },
            { id: 5, nombre: 'Ferreter√≠a General', slug: 'ferreteria-general' }
        ];
    }
}

// Obtener una categor√≠a por ID
async function getCategoriaById(id) {
    return await fetchAPI(`/categorias/${id}`);
}

// Obtener subcategor√≠as por ID de categor√≠a
async function getSubcategoriasByCategoria(categoriaId) {
    return await fetchAPI(`/categorias/${categoriaId}/subcategorias`);
}

// Obtener todos los productos (paginados)
async function getProductos(page = 1, limit = 10) {
    try {
        return await fetchAPI(`/productos?page=${page}&limit=${limit}`);
    } catch (error) {
        console.error('Error al obtener productos:', error);
        // Devolver datos de muestra
        return {
            productos: [
                { id: 1, nombre: 'Martillo Profesional', codigo: 'MART001', precio: 12990, categoria_id: 1 },
                { id: 2, nombre: 'Destornillador El√©ctrico', codigo: 'DEST001', precio: 19990, categoria_id: 3 },
                { id: 3, nombre: 'Sierra Circular', codigo: 'SIER001', precio: 89990, categoria_id: 3 },
                { id: 4, nombre: 'Cemento 25kg', codigo: 'CEM001', precio: 5990, categoria_id: 4 }
            ],
            pagination: { page: 1, limit: 10, totalItems: 4, totalPages: 1 }
        };
    }
}

