// Funci√≥n para cargar categor√≠as en la p√°gina principal
async function loadCategories() {
    try {
        const categoriesContainer = document.getElementById('categories-container');
        if (!categoriesContainer) return;
        
        // Evitar m√∫ltiples cargas
        if (categoriesContainer.dataset.loaded === 'true') {
            console.log('Las categor√≠as ya fueron cargadas');
            return;
        }
        
        // Limpiar el contenedor
        categoriesContainer.innerHTML = '<div class="loading">Cargando categor√≠as...</div>';
        
        // Obtener categor√≠as
        const categorias = await getCategorias();
        
        // Marcar como cargado
        categoriesContainer.dataset.loaded = 'true';
        
        // Si no hay datos de API o hay error, mostrar datos de muestra
        if (!categorias || !Array.isArray(categorias) || categorias.length === 0) {
            console.log('Usando categor√≠as de muestra');
            // Categor√≠as de ejemplo para mostrar algo
            mostrarCategoriasDeMuestra(categoriesContainer);
            return;
        }
        
        // Resto del c√≥digo sin cambios...
    } catch (error) {
