}

// Inicializar cuando el DOM est√© listo
document.addEventListener('DOMContentLoaded', () => {
    // Cargar categor√≠as en la p√°gina principal
    if (window.location.pathname === '/' || window.location.pathname.includes('index.html')) {
        loadCategories();
    }
    
    // Cargar productos de la categor√≠a en la p√°gina de categor√≠a
    if (window.location.pathname.includes('categorias.html')) {
        const urlParams = new URLSearchParams(window.location.search);
        const categoriaId = urlParams.get('id');
        const page = urlParams.get('page') || 1;
        
        if (categoriaId) {
            loadCategoryProducts(categoriaId, page);
        } else {
            // Si no hay categor√≠a especificada, mostrar todas las categor√≠as
            loadCategories();
        }
    }
});

